/**
 * Inventoryfulfillment Adminhtml Controller
 * 
 * @category    Magestore
 * @package     Magestore_Inventoryfulfillment
 * @author      Magestore Developer
 */
class Magestore_Inventoryfulfillment_Controller_Fulfillment extends Magestore_Inventoryplus_Controller_Action {
    /**
     * Initialize order model instance
     *
     * @param $orderId
     *
     * @return Mage_Sales_Model_Order || false
     */
    protected function _initOrder($orderId)
    {
        $order = Mage::getModel('sales/order')->load($orderId);

        if (!$order->getId()) {
            $this->_getSession()->addError($this->__('This order no longer exists.'));
            $this->_redirect('*/*/');
            $this->setFlag('', self::FLAG_NO_DISPATCH, true);
            return false;
        }
        Mage::register('sales_order', $order);
        Mage::register('current_order', $order);
        return $order;
    }
    
    protected function _helper(){
        return Mage::helper('inventoryfulfillment');
    }
}